import React, { useState, useEffect } from 'react';

const TasksPage = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  // טוען משימות מ-Local Storage בעת טעינת הקומפוננטה
  useEffect(() => {
    try {
      const savedTasks = localStorage.getItem('tasks');
      if (savedTasks) {
        setTasks(JSON.parse(savedTasks));
      }
    } catch (error) {
      console.error('Failed to load tasks from Local Storage:', error);
    }
  }, []);

  // שומר משימות ב-Local Storage בכל שינוי ברשימה
  const saveTasksToLocalStorage = (updatedTasks) => {
    try {
      localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    } catch (error) {
      console.error('Failed to save tasks to Local Storage:', error);
    }
  };

  // הוספת משימה חדשה
  const addTask = () => {
    if (newTask.trim() === '') return;
    const updatedTasks = [
      ...tasks,
      { id: Date.now(), text: newTask, completed: false },
    ];
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
    setNewTask('');
  };

  // מחיקת משימה
  const deleteTask = (id) => {
    const updatedTasks = tasks.filter((task) => task.id !== id);
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
  };

  // סימון משימה כבוצעה (פעם אחת בלבד)
  const markTaskAsCompleted = (id) => {
    const updatedTasks = tasks.map((task) =>
      task.id === id && !task.completed
        ? { ...task, completed: true }
        : task
    );
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">ניהול משימות יומיות</h1>
      <div className="mb-4 flex gap-2">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="הוסף משימה חדשה"
          className="border p-2 flex-1"
        />
        <button
          onClick={addTask}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          הוסף
        </button>
      </div>
      <ul className="list-disc pl-5">
        {tasks.map((task) => (
          <li
            key={task.id}
            className={`flex justify-between items-center p-2 border-b ${
              task.completed ? 'line-through text-gray-500' : ''
            }`}
          >
            <span>{task.text}</span>
            <div className="flex gap-2">
              {!task.completed && (
                <button
                  onClick={() => markTaskAsCompleted(task.id)}
                  className="bg-green-500 text-white px-2 py-1 rounded"
                >
                  הושלם
                </button>
              )}
              <button
                onClick={() => deleteTask(task.id)}
                className="bg-red-500 text-white px-2 py-1 rounded"
              >
                מחק
              </button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TasksPage;
