import React, { useState, useEffect } from 'react';

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [priority, setPriority] = useState('נמוכה');

  useEffect(() => {
    const savedTasks = localStorage.getItem('tasks');
    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    }
  }, []);

  const addTask = () => {
    if (newTask.trim() === '') return;
    const currentDate = new Date().toLocaleDateString('he-IL');
    const newTaskItem = {
      text: newTask,
      priority,
      completed: false,
      date: currentDate,
    };
    setTasks([...tasks, newTaskItem]);
    setNewTask('');
    localStorage.setItem('tasks', JSON.stringify([...tasks, newTaskItem]));
  };

  const toggleComplete = (index) => {
    const updatedTasks = tasks.map((task, i) =>
      i === index ? { ...task, completed: !task.completed } : task
    );
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
  };

  const deleteTask = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
  };

  return (
    <div className="container mx-auto p-8 bg-white rounded-md shadow-md max-w-5xl">
      {/* כותרת */}
      <h1 className="text-3xl font-bold mb-6 text-center">📋 ניהול משימות יומיות</h1>
      
      {/* הוספת משימה */}
      <div className="flex gap-4 mb-6">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="דוגמה: לסיים פרק בספר עד סוף היום."
          className="flex-1 border p-2 rounded-md"
        />
        <select
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
          className="border p-2 rounded-md"
        >
          <option value="נמוכה">נמוכה</option>
          <option value="בינונית">בינונית</option>
          <option value="גבוהה">גבוהה</option>
        </select>
        <button
          onClick={addTask}
          className="bg-blue-500 text-white px-4 py-2 rounded-md"
        >
          ➕ הוסף
        </button>
      </div>

      {/* רשימת משימות */}
      <ul className="space-y-4">
        {tasks.map((task, index) => (
          <li
            key={index}
            className={`p-4 rounded-md shadow-md ${
              task.completed ? 'bg-green-100' : 'bg-gray-100'
            }`}
          >
            <div className="flex justify-between items-center">
              <span
                className={`${
                  task.completed ? 'line-through text-gray-500' : ''
                }`}
              >
                {task.text}
              </span>
              <div className="flex gap-2">
                <button
                  onClick={() => toggleComplete(index)}
                  className={`px-3 py-1 rounded-md ${
                    task.completed
                      ? 'bg-gray-500 text-white'
                      : 'bg-green-500 text-white'
                  }`}
                >
                  {task.completed ? '✔️ הושלם' : '✔️ סמן כהושלם'}
                </button>
                <button
                  onClick={() => deleteTask(index)}
                  className="bg-red-500 text-white px-3 py-1 rounded-md"
                >
                  🗑️ מחק
                </button>
              </div>
            </div>
            {/* תאריך הצגת המשימה */}
            <div className="text-sm text-gray-500 mt-2 text-right">
              📅 תאריך יצירה: {task.date}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Tasks;
