import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const TasksPage = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [priority, setPriority] = useState('low'); // דחיפות כברירת מחדל

  // טוען משימות מ-Local Storage בעת טעינת הקומפוננטה
  useEffect(() => {
    try {
      const savedTasks = localStorage.getItem('tasks');
      if (savedTasks) {
        setTasks(JSON.parse(savedTasks));
      }
    } catch (error) {
      console.error('Failed to load tasks from Local Storage:', error);
    }
  }, []);

  // שומר משימות ב-Local Storage בכל שינוי ברשימה
  const saveTasksToLocalStorage = (updatedTasks) => {
    try {
      localStorage.setItem('tasks', JSON.stringify(updatedTasks));
    } catch (error) {
      console.error('Failed to save tasks to Local Storage:', error);
    }
  };

  // הוספת משימה חדשה
  const addTask = () => {
    if (newTask.trim() === '') return;
    const updatedTasks = [
      ...tasks,
      {
        id: Date.now(),
        text: newTask,
        completed: false,
        priority,
      },
    ];
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
    setNewTask('');
    setPriority('low');
  };

  // מחיקת משימה
  const deleteTask = (id) => {
    const updatedTasks = tasks.filter((task) => task.id !== id);
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
  };

  // סימון משימה כהושלמה
  const markTaskAsCompleted = (id) => {
    const updatedTasks = tasks.map((task) =>
      task.id === id ? { ...task, completed: true } : task
    );
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
  };

  // הגדרת צבע לפי עדיפות
  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'bg-red-500';
      case 'medium':
        return 'bg-yellow-500';
      case 'low':
        return 'bg-green-500';
      default:
        return 'bg-gray-300';
    }
  };

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">ניהול משימות יומיות</h1>
      <div className="mb-4 flex gap-2">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="הוסף משימה חדשה"
          className="border p-2 flex-1"
        />
        <select
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
          className="border p-2"
        >
          <option value="low">נמוכה</option>
          <option value="medium">בינונית</option>
          <option value="high">גבוהה</option>
        </select>
        <button
          onClick={addTask}
          className="bg-blue-500 text-white px-4 py-2 rounded"
        >
          הוסף
        </button>
      </div>
      <ul className="list-disc pl-5">
        <AnimatePresence>
          {tasks.map((task) => (
            <motion.li
              key={task.id}
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.3 }}
              className={`flex justify-between items-center p-2 border-b ${
                task.completed ? 'line-through text-gray-500' : ''
              }`}
            >
              <span>{task.text}</span>
              <div className="flex gap-2 items-center">
                <span
                  className={`px-2 py-1 text-white text-sm rounded ${getPriorityColor(
                    task.priority
                  )}`}
                >
                  {task.priority === 'high'
                    ? 'דחוף'
                    : task.priority === 'medium'
                    ? 'בינוני'
                    : 'נמוך'}
                </span>
                {!task.completed && (
                  <button
                    onClick={() => markTaskAsCompleted(task.id)}
                    className="bg-green-500 text-white px-2 py-1 rounded"
                  >
                    הושלם
                  </button>
                )}
                <button
                  onClick={() => deleteTask(task.id)}
                  className="bg-red-500 text-white px-2 py-1 rounded"
                >
                  מחק
                </button>
              </div>
            </motion.li>
          ))}
        </AnimatePresence>
      </ul>
    </div>
  );
};

export default TasksPage;
