import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import { onAuthStateChanged } from "firebase/auth";
import { auth } from "./firebase";

import Login from "./pages/Login";
import SignUp from "./pages/SignUp";
import Home from "./pages/Home";
import Gratitude from './pages/Gratitude';
import PositiveLanguage from "./pages/PositiveLanguage";
import Tasks from "./pages/Tasks";

import PrivateRoute from "./components/PrivateRoute";

function App() {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // מאזין לשינויים במצב האימות
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
      setLoading(false);
    });
    return () => unsubscribe(); // מנקה את המאזין כאשר הקומפוננטה יוצאת משימוש
  }, []);

  if (loading) {
    return <div>טוען...</div>; // מציג מסך טעינה זמני
  }

  return (
    <Router>
      <Routes>
        {/* נתיבים ציבוריים */}
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<SignUp />} />

        {/* נתיבים פרטיים מוגנים */}
        <Route
          path="/home"
          element={
            <PrivateRoute>
              <Home />
            </PrivateRoute>
          }
        />
        <Route
          path="/gratitude"
          element={
            <PrivateRoute>
              <Gratitude />
            </PrivateRoute>
          }
        />
        <Route
          path="/positive-language"
          element={
            <PrivateRoute>
              <PositiveLanguage />
            </PrivateRoute>
          }
        />
        <Route
          path="/tasks"
          element={
            <PrivateRoute>
              <Tasks />
            </PrivateRoute>
          }
        />

        {/* ברירת מחדל: ניתוב לדף התחברות */}
        <Route path="/" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
}

export default App;
