import React, { useState } from 'react';
import { saveGratitudes } from '../services/gratitudeService';

const Gratitude = () => {
  const [gratitude1, setGratitude1] = useState('');
  const [gratitude2, setGratitude2] = useState('');
  const [gratitude3, setGratitude3] = useState('');

  const handleSave = async () => {
    const gratitudes = [gratitude1, gratitude2, gratitude3];
    await saveGratitudes(gratitudes);
  };

  return (
    <div>
      <h1>🙏 הכרת תודה יומית</h1>
      <p>כתוב שלושה דברים שעליהם אתה מודה היום:</p>
      <input
        type="text"
        value={gratitude1}
        onChange={(e) => setGratitude1(e.target.value)}
        placeholder="דבר מס' 1"
      />
      <input
        type="text"
        value={gratitude2}
        onChange={(e) => setGratitude2(e.target.value)}
        placeholder="דבר מס' 2"
      />
      <input
        type="text"
        value={gratitude3}
        onChange={(e) => setGratitude3(e.target.value)}
        placeholder="דבר מס' 3"
      />
      <button onClick={handleSave}>שמור תודות</button>
    </div>
  );
};

export default Gratitude;
