import React, { useState } from "react";
import { saveGratitudes } from "../services/gratitudeService";

const Gratitude = () => {
  const [gratitudes, setGratitudes] = useState(["", "", ""]);

  const handleSaveGratitudes = async () => {
    try {
      await saveGratitudes(gratitudes);
    } catch (error) {
      console.error("❌ שגיאה בשמירת תודות:", error);
    }
  };

  return (
    <div>
      <h1>🙏 הכרת תודה יומית</h1>
      {gratitudes.map((gratitude, index) => (
        <input
          key={index}
          value={gratitude}
          onChange={(e) => {
            const newGratitudes = [...gratitudes];
            newGratitudes[index] = e.target.value;
            setGratitudes(newGratitudes);
          }}
          placeholder={`תודה מס' ${index + 1}`}
        />
      ))}
      <button onClick={handleSaveGratitudes}>שמור תודות</button>
    </div>
  );
};

export default Gratitude;
