import React, { useState, useEffect } from 'react';

const PositiveLanguage = () => {
  const [affirmations, setAffirmations] = useState([]); // רשימת הצהרות
  const [newAffirmation, setNewAffirmation] = useState(''); // הצהרה חדשה
  const [editingIndex, setEditingIndex] = useState(null); // אינדקס לעריכה
  const [editText, setEditText] = useState(''); // טקסט בעריכה

  // טוען הצהרות מ-Local Storage
  useEffect(() => {
    const savedAffirmations = localStorage.getItem('affirmations');
    if (savedAffirmations) {
      setAffirmations(JSON.parse(savedAffirmations));
    }
  }, []);

  // שומר הצהרות ב-Local Storage
  useEffect(() => {
    localStorage.setItem('affirmations', JSON.stringify(affirmations));
  }, [affirmations]);

  // הוספת הצהרה חדשה
  const addAffirmation = () => {
    if (newAffirmation.trim() === '') return;
    setAffirmations([...affirmations, newAffirmation]);
    setNewAffirmation('');
  };

  // מחיקת הצהרה
  const deleteAffirmation = (index) => {
    const updatedAffirmations = affirmations.filter((_, i) => i !== index);
    setAffirmations(updatedAffirmations);
  };

  // עריכת הצהרה
  const startEditing = (index) => {
    setEditingIndex(index);
    setEditText(affirmations[index]);
  };

  const saveEdit = () => {
    const updatedAffirmations = [...affirmations];
    updatedAffirmations[editingIndex] = editText;
    setAffirmations(updatedAffirmations);
    setEditingIndex(null);
    setEditText('');
  };

  return (
    <div className="container mx-auto p-8 bg-white rounded-md shadow-md max-w-5xl">
      {/* כותרת */}
      <h1 className="text-3xl font-bold mb-6 text-center">💬 תרגול שפה חיובית</h1>
      <p className="text-gray-600 text-center mb-8">
        כתבו ושמרו הצהרות חיוביות שיעזרו לכם לחזק את הביטחון העצמי והגישה החיובית.
      </p>

      {/* הוספת הצהרה */}
      <div className="flex gap-4 mb-6">
        <input
          type="text"
          value={newAffirmation}
          onChange={(e) => setNewAffirmation(e.target.value)}
          placeholder="כתבו הצהרה חיובית..."
          className="flex-1 border p-2 rounded-md"
        />
        <button
          onClick={addAffirmation}
          className="bg-green-500 text-white px-4 py-2 rounded-md hover:bg-green-600"
        >
          ➕ הוסף
        </button>
      </div>

      {/* רשימת הצהרות */}
      <ul className="space-y-4">
        {affirmations.map((affirmation, index) => (
          <li
            key={index}
            className="border p-4 rounded-md shadow-md flex justify-between items-center"
          >
            {editingIndex === index ? (
              <>
                <input
                  type="text"
                  value={editText}
                  onChange={(e) => setEditText(e.target.value)}
                  className="flex-1 border p-2 rounded-md mr-2"
                />
                <button
                  onClick={saveEdit}
                  className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
                >
                  💾 שמור
                </button>
              </>
            ) : (
              <>
                <span>{affirmation}</span>
                <div className="flex gap-2">
                  <button
                    onClick={() => startEditing(index)}
                    className="bg-yellow-500 text-white px-3 py-1 rounded-md hover:bg-yellow-600"
                  >
                    ✏️ ערוך
                  </button>
                  <button
                    onClick={() => deleteAffirmation(index)}
                    className="bg-red-500 text-white px-3 py-1 rounded-md hover:bg-red-600"
                  >
                    🗑️ מחק
                  </button>
                </div>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default PositiveLanguage;
