import React, { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const TasksPage = () => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [priority, setPriority] = useState('low');
  const [dueDate, setDueDate] = useState('');
  const [editTaskId, setEditTaskId] = useState(null);

  useEffect(() => {
    const savedTasks = localStorage.getItem('tasks');
    if (savedTasks) {
      setTasks(JSON.parse(savedTasks));
    }
  }, []);

  const saveTasksToLocalStorage = (updatedTasks) => {
    localStorage.setItem('tasks', JSON.stringify(updatedTasks));
  };

  // הוספת משימה חדשה
  const addTask = () => {
    if (newTask.trim() === '') return;
    const updatedTasks = [
      ...tasks,
      {
        id: Date.now(),
        text: newTask,
        completed: false,
        priority,
        dueDate,
      },
    ];
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
    setNewTask('');
    setPriority('low');
    setDueDate('');
  };

  // עריכת משימה
  const editTask = (id) => {
    const taskToEdit = tasks.find((task) => task.id === id);
    setNewTask(taskToEdit.text);
    setPriority(taskToEdit.priority);
    setDueDate(taskToEdit.dueDate);
    setEditTaskId(id);
  };

  const updateTask = () => {
    const updatedTasks = tasks.map((task) =>
      task.id === editTaskId
        ? {
            ...task,
            text: newTask,
            priority,
            dueDate,
          }
        : task
    );
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
    setNewTask('');
    setPriority('low');
    setDueDate('');
    setEditTaskId(null);
  };

  // מחיקת משימה
  const deleteTask = (id) => {
    const updatedTasks = tasks.filter((task) => task.id !== id);
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
  };

  // סימון משימה כהושלמה
  const markTaskAsCompleted = (id) => {
    const updatedTasks = tasks.map((task) =>
      task.id === id ? { ...task, completed: true } : task
    );
    setTasks(updatedTasks);
    saveTasksToLocalStorage(updatedTasks);
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return 'bg-red-200';
      case 'medium':
        return 'bg-yellow-200';
      case 'low':
        return 'bg-green-200';
      default:
        return 'bg-gray-200';
    }
  };

  return (
    <div className="container mx-auto p-4 max-w-xl">
      <h1 className="text-2xl font-bold mb-4 text-center">ניהול משימות יומיות</h1>
      <div className="mb-4 flex gap-2">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="הוסף או ערוך משימה"
          className="border p-2 flex-1 rounded-md"
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
          className="border p-2 rounded-md"
        />
        <select
          value={priority}
          onChange={(e) => setPriority(e.target.value)}
          className="border p-2 rounded-md"
        >
          <option value="low">נמוכה</option>
          <option value="medium">בינונית</option>
          <option value="high">גבוהה</option>
        </select>
        {editTaskId ? (
          <button
            onClick={updateTask}
            className="bg-yellow-500 text-white px-4 py-2 rounded-md"
          >
            עדכן
          </button>
        ) : (
          <button
            onClick={addTask}
            className="bg-blue-500 text-white px-4 py-2 rounded-md"
          >
            הוסף
          </button>
        )}
      </div>
      <ul>
        <AnimatePresence>
          {tasks.map((task) => (
            <motion.li
              key={task.id}
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -10 }}
              transition={{ duration: 0.3 }}
              className={`flex justify-between items-center p-3 mb-2 rounded-md shadow-md ${getPriorityColor(
                task.priority
              )}`}
            >
              <div>
                <span className={`flex-1 ${task.completed ? 'line-through' : ''}`}>
                  {task.text} ({task.dueDate || 'ללא תאריך'})
                </span>
                {new Date(task.dueDate) < new Date() && !task.completed && (
                  <span className="text-red-500 text-sm">פג תוקף</span>
                )}
              </div>
              <div className="flex gap-2">
                <button onClick={() => editTask(task.id)}>✏️</button>
                {!task.completed && (
                  <button
                    onClick={() => markTaskAsCompleted(task.id)}
                    className="bg-green-500 text-white px-2 py-1 rounded-md"
                  >
                    ✓ הושלם
                  </button>
                )}
                <button
                  onClick={() => deleteTask(task.id)}
                  className="bg-red-500 text-white px-2 py-1 rounded-md"
                >
                  🗑️ מחק
                </button>
              </div>
            </motion.li>
          ))}
        </AnimatePresence>
      </ul>
    </div>
  );
};

export default TasksPage;
