import React, { useEffect, useState } from "react";
import { fetchGratitudes } from "../services/gratitudeService";

const Gratitude = () => {
  const [gratitudes, setGratitudes] = useState([]);

  useEffect(() => {
    const loadGratitudes = async () => {
      const data = await fetchGratitudes();
      setGratitudes(data);
    };

    loadGratitudes();
  }, []);

  return (
    <div>
      <h1>🙏 הכרת תודה יומית</h1>
      <h3>דברים שעליהם אתה מודה היום:</h3>
      <ul>
        {gratitudes.length > 0 ? (
          gratitudes.map((item) => (
            <li key={item.id}>
              <strong>תאריך:</strong> {new Date(item.date).toLocaleString()}
              <ul>
                {item.gratitudes.map((gratitude, index) => (
                  <li key={index}>{gratitude}</li>
                ))}
              </ul>
            </li>
          ))
        ) : (
          <p>לא נמצאו תודות להצגה.</p>
        )}
      </ul>
    </div>
  );
};

export default Gratitude;
