// services/gratitudeService.js

import { addDoc, collection } from "firebase/firestore";
import { auth, db } from "../firebase";

/**
 * שומר תודות ב-Firestore
 * @param {Array<string>} gratitudes - מערך של תודות
 */
export const saveGratitudes = async (gratitudes) => {
  try {
    // ודא שהמשתמש מחובר
    if (!auth.currentUser) {
      console.error("⚠️ המשתמש אינו מחובר!");
      return;
    }

    // יצירת אובייקט נתונים
    const gratitudeData = {
      userId: auth.currentUser.uid,
      gratitudes,
      date: new Date().toISOString(),
    };

    // הדפסת הנתונים לבדיקה
    console.log("🔥 נתונים שנשלחים ל-Firestore:", gratitudeData);

    // שליחה ל-Firestore
    await addDoc(collection(db, "gratitude"), gratitudeData);

    console.log("✅ תודות נשמרו בהצלחה!");
  } catch (error) {
    console.error("❌ שגיאה בשליחת התודות:", error);
  }
};
