import React, { useState, useEffect } from 'react';

const VisionPage = () => {
  const [visionItems, setVisionItems] = useState([]); // פריטים בלוח חזון
  const [newTitle, setNewTitle] = useState(''); // כותרת חדשה
  const [newDescription, setNewDescription] = useState(''); // תיאור חדש
  const [activeTab, setActiveTab] = useState('weekly'); // קטגוריית יעדים
  const [isEditing, setIsEditing] = useState(true);

  // 🚀 טעינת נתונים מה־localStorage
  useEffect(() => {
    const savedVision = localStorage.getItem('visionBoard');
    if (savedVision) {
      setVisionItems(JSON.parse(savedVision));
    }
  }, []);

  // 💾 שמירת הנתונים ב־localStorage
  const saveToLocalStorage = (items) => {
    localStorage.setItem('visionBoard', JSON.stringify(items));
  };

  // ➕ הוספת פריט ללוח החזון
  const addItem = () => {
    if (newTitle.trim() === '' || newDescription.trim() === '') {
      alert('נא למלא כותרת ותיאור!');
      return;
    }

    const newItem = {
      id: Date.now(), // מזהה ייחודי
      title: newTitle,
      description: newDescription,
      category: activeTab,
    };

    const updatedItems = [...visionItems, newItem];
    setVisionItems(updatedItems);
    saveToLocalStorage(updatedItems);
    setNewTitle('');
    setNewDescription('');
  };

  // 🗑️ מחיקת פריט מהלוח
  const deleteItem = (id) => {
    const updatedItems = visionItems.filter((item) => item.id !== id);
    setVisionItems(updatedItems);
    saveToLocalStorage(updatedItems);
  };

  // 🔄 איפוס הלוח
  const resetVision = () => {
    setVisionItems([]);
    localStorage.removeItem('visionBoard');
    alert('🗑️ לוח החזון נמחק בהצלחה!');
  };

  return (
    <div className="container mx-auto p-8 bg-white rounded-md shadow-md max-w-5xl">
      {/* כותרת */}
      <h1 className="text-3xl font-bold mb-6 text-center">🌟 חזון אישי ויעדים 🌟</h1>

      {/* כפתורי פעולה */}
      <div className="flex gap-4 justify-center mb-6">
        <button onClick={addItem} className="bg-green-500 text-white px-4 py-2 rounded-md">
          ➕ הוסף פריט
        </button>
        <button onClick={resetVision} className="bg-red-500 text-white px-4 py-2 rounded-md">
          🗑️ איפוס הלוח
        </button>
        <button onClick={() => setIsEditing(!isEditing)} className="bg-yellow-500 text-white px-4 py-2 rounded-md">
          ✏️ {isEditing ? 'סיים עריכה' : 'ערוך'}
        </button>
      </div>

      {/* הוספת פריט חדש */}
      <div className="mb-6 border p-4 rounded-md shadow-sm bg-gray-50">
        <h2 className="text-xl font-bold mb-2">➕ הוספת פריט חדש</h2>
        <input
          type="text"
          placeholder="כותרת"
          value={newTitle}
          onChange={(e) => setNewTitle(e.target.value)}
          className="w-full p-2 border rounded-md mb-2"
          disabled={!isEditing}
        />
        <textarea
          placeholder="תיאור"
          value={newDescription}
          onChange={(e) => setNewDescription(e.target.value)}
          className="w-full p-2 border rounded-md mb-2"
          disabled={!isEditing}
        />
        <button onClick={addItem} className="bg-blue-500 text-white px-4 py-2 rounded-md" disabled={!isEditing}>
          ➕ שמור פריט
        </button>
      </div>

      {/* קטגוריות יעדים */}
      <div className="mb-6">
        <h2 className="text-xl font-bold mb-2">🎯 יעדים</h2>
        <div className="flex gap-4 mb-4">
          <button
            onClick={() => setActiveTab('weekly')}
            className={`px-4 py-2 rounded-md ${
              activeTab === 'weekly' ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
          >
            🗓️ שבועיים
          </button>
          <button
            onClick={() => setActiveTab('monthly')}
            className={`px-4 py-2 rounded-md ${
              activeTab === 'monthly' ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
          >
            📅 חודשיים
          </button>
          <button
            onClick={() => setActiveTab('yearly')}
            className={`px-4 py-2 rounded-md ${
              activeTab === 'yearly' ? 'bg-blue-500 text-white' : 'bg-gray-200'
            }`}
          >
            🏆 שנתיים
          </button>
        </div>

        {/* הצגת פריטים לפי קטגוריה */}
        <div className="space-y-4">
          {visionItems
            .filter((item) => item.category === activeTab)
            .map((item) => (
              <div key={item.id} className="p-4 border rounded-md shadow-md bg-white relative">
                <h3 className="text-xl font-bold mb-2">{item.title}</h3>
                <p className="text-gray-600">{item.description}</p>
                {isEditing && (
                  <button
                    onClick={() => deleteItem(item.id)}
                    className="bg-red-500 text-white px-2 py-1 rounded-md absolute top-2 right-2 hover:bg-red-600"
                  >
                    🗑️ מחק
                  </button>
                )}
              </div>
            ))}
        </div>
      </div>
    </div>
  );
};

export default VisionPage;
