import React, { useState, useEffect } from 'react';

const VisionPage = () => {
  const [visionText, setVisionText] = useState('');
  const [weeklyGoals, setWeeklyGoals] = useState('');
  const [monthlyGoals, setMonthlyGoals] = useState('');
  const [yearlyGoals, setYearlyGoals] = useState('');
  const [inspirationImages, setInspirationImages] = useState([]);
  const [quotes, setQuotes] = useState('');
  const [isEditing, setIsEditing] = useState(true); // מצב עריכה או תצוגה

  useEffect(() => {
    const savedVision = localStorage.getItem('visionData');
    if (savedVision) {
      const data = JSON.parse(savedVision);
      setVisionText(data.visionText || '');
      setWeeklyGoals(data.weeklyGoals || '');
      setMonthlyGoals(data.monthlyGoals || '');
      setYearlyGoals(data.yearlyGoals || '');
      setInspirationImages(data.inspirationImages || []);
      setQuotes(data.quotes || '');
      setIsEditing(false); // מצב צפייה בנתונים
    }
  }, []);

  const saveVision = () => {
    const visionData = {
      visionText,
      weeklyGoals,
      monthlyGoals,
      yearlyGoals,
      inspirationImages,
      quotes,
    };
    localStorage.setItem('visionData', JSON.stringify(visionData));
    setIsEditing(false); // עובר למצב תצוגה
    alert('💾 החזון נשמר בהצלחה!');
  };

  const resetVision = () => {
    setVisionText('');
    setWeeklyGoals('');
    setMonthlyGoals('');
    setYearlyGoals('');
    setInspirationImages([]);
    setQuotes('');
    localStorage.removeItem('visionData');
    setIsEditing(true); // חוזר למצב עריכה
    alert('🗑️ החזון נמחק בהצלחה!');
  };

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setInspirationImages([...inspirationImages, reader.result]);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-3xl font-bold mb-6 text-center">🌟 חזון אישי ויעדים 🌟</h1>

      {/* כפתורי פעולה */}
      <div className="flex gap-4 justify-center mb-6">
        <button
          onClick={saveVision}
          className="bg-blue-500 text-white px-4 py-2 rounded-md"
          disabled={!isEditing}
        >
          💾 שמור
        </button>
        <button
          onClick={resetVision}
          className="bg-red-500 text-white px-4 py-2 rounded-md"
        >
          🗑️ מחק
        </button>
        <button
          onClick={() => setIsEditing(true)}
          className="bg-yellow-500 text-white px-4 py-2 rounded-md"
          disabled={isEditing}
        >
          ✏️ ערוך
        </button>
      </div>

      {/* חזון אישי */}
      <section className="mb-6">
        <h2 className="text-xl font-bold mb-2">📜 כתיבת חזון אישי</h2>
        <textarea
          value={visionText}
          onChange={(e) => setVisionText(e.target.value)}
          placeholder="כתוב את החזון האישי שלך כאן..."
          className="w-full h-32 p-2 border rounded-md"
          disabled={!isEditing}
        />
      </section>

      {/* יעדים */}
      <section className="mb-6">
        <h2 className="text-xl font-bold mb-2">🎯 יעדים</h2>
        <div className="mb-2">
          <label>🗓️ יעדים שבועיים:</label>
          <input
            type="text"
            value={weeklyGoals}
            onChange={(e) => setWeeklyGoals(e.target.value)}
            placeholder="הזן את היעדים השבועיים שלך"
            className="w-full p-2 border rounded-md"
            disabled={!isEditing}
          />
        </div>
        <div className="mb-2">
          <label>📅 יעדים חודשיים:</label>
          <input
            type="text"
            value={monthlyGoals}
            onChange={(e) => setMonthlyGoals(e.target.value)}
            placeholder="הזן את היעדים החודשיים שלך"
            className="w-full p-2 border rounded-md"
            disabled={!isEditing}
          />
        </div>
        <div className="mb-2">
          <label>🏆 יעדים שנתיים:</label>
          <input
            type="text"
            value={yearlyGoals}
            onChange={(e) => setYearlyGoals(e.target.value)}
            placeholder="הזן את היעדים השנתיים שלך"
            className="w-full p-2 border rounded-md"
            disabled={!isEditing}
          />
        </div>
      </section>

      {/* תמונות השראה */}
      <section className="mb-6">
        <h2 className="text-xl font-bold mb-2">🖼️ תמונות השראה</h2>
        <input type="file" accept="image/*" onChange={handleImageUpload} disabled={!isEditing} />
        <div className="flex gap-4 mt-4">
          {inspirationImages.map((img, index) => (
            <img
              key={index}
              src={img}
              alt="inspiration"
              className="w-24 h-24 rounded-md shadow-md"
            />
          ))}
        </div>
      </section>

      {/* ציטוטים */}
      <section className="mb-6">
        <h2 className="text-xl font-bold mb-2">💬 ציטוטים מעוררי השראה</h2>
        <textarea
          value={quotes}
          onChange={(e) => setQuotes(e.target.value)}
          placeholder="הוסף ציטוטים שמעוררים בך השראה..."
          className="w-full h-24 p-2 border rounded-md"
          disabled={!isEditing}
        />
      </section>
    </div>
  );
};

export default VisionPage;
