import { collection, getDocs } from "firebase/firestore";
import { db, auth } from "../firebase";

export const fetchGratitudes = async () => {
  try {
    if (!auth.currentUser) {
      console.error("המשתמש אינו מחובר");
      return [];
    }

    const querySnapshot = await getDocs(collection(db, "gratitude"));
    const gratitudes = [];
    
    querySnapshot.forEach((doc) => {
      const data = doc.data();
      if (data.userId === auth.currentUser.uid) {
        gratitudes.push({
          id: doc.id,
          ...data,
        });
      }
    });

    console.log("נתוני תודות נשלפו בהצלחה:", gratitudes);
    return gratitudes;
  } catch (error) {
    console.error("שגיאה בשליפת התודות:", error.message);
    return [];
  }
};
