let tasks = loadTasksFromLocalStorage();

// פונקציה להוספת משימה
const addTask = (task) => {
    tasks.push(task);
    saveTasksToLocalStorage(tasks);
    renderTasks();
};

// פונקציה להצגת המשימות
const renderTasks = () => {
    const taskList = document.getElementById('taskList');
    taskList.innerHTML = '';
    tasks.forEach((task, index) => {
        taskList.innerHTML += `<li>${task} <button onclick="removeTask(${index})">מחק</button></li>`;
    });
};

// פונקציה להסרת משימה
const removeTask = (index) => {
    tasks.splice(index, 1);
    saveTasksToLocalStorage(tasks);
    renderTasks();
};

// הצגת המשימות עם טעינת העמוד
renderTasks();
