import React, { useState, useEffect } from 'react';
import './tasks.css';

function TaskManager() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [dueDate, setDueDate] = useState('');
  const [priority, setPriority] = useState('בינונית');

  // 📥 טוען משימות מ-Local Storage בעת טעינת הדף
  useEffect(() => {
    const savedTasks = JSON.parse(localStorage.getItem('tasks')) || [];
    setTasks(savedTasks);
  }, []);

  // 📤 שומר משימות ל-Local Storage בכל שינוי ברשימת המשימות
  useEffect(() => {
    localStorage.setItem('tasks', JSON.stringify(tasks));
  }, [tasks]);

  // ➕ הוספת משימה חדשה
  const addTask = () => {
    if (!newTask.trim()) return;

    const task = {
      id: Date.now(),
      title: newTask,
      dueDate,
      priority,
      completed: false,
    };

    setTasks([...tasks, task]);
    setNewTask('');
    setDueDate('');
    setPriority('בינונית');
  };

  // ✅ סימון משימה כהושלמה
  const toggleTask = (id) => {
    setTasks(tasks.map(task =>
      task.id === id ? { ...task, completed: !task.completed } : task
    ));
  };

  // 🗑️ מחיקת משימה
  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  return (
    <div>
      <h2>📝 ניהול משימות</h2>
      <div className="task-input-container">
        <input
          type="text"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
          placeholder="כותרת המשימה"
        />
        <input
          type="date"
          value={dueDate}
          onChange={(e) => setDueDate(e.target.value)}
        />
        <select value={priority} onChange={(e) => setPriority(e.target.value)}>
          <option value="גבוהה">גבוהה</option>
          <option value="בינונית">בינונית</option>
          <option value="נמוכה">נמוכה</option>
        </select>
        <button onClick={addTask}>➕ הוסף משימה</button>
      </div>
      {tasks.length === 0 ? (
        <p className="no-tasks">📭 אין משימות להצגה</p>
      ) : (
        <ul className="task-list">
          {tasks.map(task => (
            <li key={task.id} className={task.completed ? 'completed' : ''}>
              <div>
                <input
                  type="checkbox"
                  checked={task.completed}
                  onChange={() => toggleTask(task.id)}
                />
                <strong>{task.title}</strong> | יעד: {task.dueDate} | עדיפות: {task.priority}
              </div>
              <button onClick={() => deleteTask(task.id)}>🗑️</button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default TaskManager;
