import React, { useState } from 'react';

const quotes = [
  "🌟 'שפע הוא מצב תודעתי, לא מצב חשבון בנק.' - רובין שארמה",
  "💬 'המחשבות שלך יוצרות את המציאות שלך.' - אלברט איינשטיין",
  "📚 'הדרך הטובה ביותר לחזות את העתיד היא ליצור אותו.' - אברהם לינקולן",
  "❤️ 'כל יום הוא הזדמנות להתחיל מחדש עם תודעה של שפע.'",
  "🌱 'זרע קטן של הכרת תודה יכול לצמוח לעץ של הצלחה.'"
];

const Home = () => {
  const [currentQuoteIndex, setCurrentQuoteIndex] = useState(0);

  const nextQuote = () => {
    setCurrentQuoteIndex((prevIndex) => (prevIndex + 1) % quotes.length);
  };

  const prevQuote = () => {
    setCurrentQuoteIndex((prevIndex) => (prevIndex - 1 + quotes.length) % quotes.length);
  };

  return (
    <div className="container mx-auto p-8">
      <h1 className="text-4xl font-bold mb-6 text-center">🌟 ברוך הבא לאתר תודעת שפע 🌟</h1>
      <p className="text-center text-gray-600 mb-6">
        האתר נועד לעזור לך לפתח תודעת שפע, להציב יעדים ולהגשים חלומות. התחל היום!
      </p>

      {/* תצוגת ציטוטים */}
      <div className="bg-blue-100 p-6 rounded-md shadow-md mb-6 text-center relative">
        <button
          onClick={prevQuote}
          className="absolute left-2 top-1/2 transform -translate-y-1/2 bg-gray-300 hover:bg-gray-400 px-2 py-1 rounded-md"
        >
          ◀️
        </button>
        <p className="text-xl italic text-gray-800">{quotes[currentQuoteIndex]}</p>
        <button
          onClick={nextQuote}
          className="absolute right-2 top-1/2 transform -translate-y-1/2 bg-gray-300 hover:bg-gray-400 px-2 py-1 rounded-md"
        >
          ▶️
        </button>
      </div>

      {/* תפריט עמודים מרכזיים */}
      <div className="grid grid-cols-2 gap-6 text-center">
        <div className="p-4 border rounded-md shadow-md hover:bg-gray-100">
          🌼 <strong>הכרת תודה יומית</strong>
          <p>התחילו כל יום עם מחשבה חיובית.</p>
        </div>
        <div className="p-4 border rounded-md shadow-md hover:bg-gray-100">
          💬 <strong>שפה חיובית</strong>
          <p>למדו להשתמש בשפה שמעצימה אתכם.</p>
        </div>
        <div className="p-4 border rounded-md shadow-md hover:bg-gray-100">
          ✅ <strong>ניהול משימות</strong>
          <p>ארגנו את המשימות שלכם בצורה יעילה.</p>
        </div>
        <div className="p-4 border rounded-md shadow-md hover:bg-gray-100">
          🎯 <strong>חזון אישי ויעדים</strong>
          <p>כתבו את החזון והיעדים שלכם.</p>
        </div>
      </div>
    </div>
  );
};

export default Home;
