import React, { useEffect, useState } from 'react';

const VisionSummary = () => {
  const [achievements, setAchievements] = useState([]);
  const [insights, setInsights] = useState('');
  const [progressData, setProgressData] = useState({
    weekly: { progress: 0, summary: '' },
    monthly: { progress: 0, summary: '' },
    yearly: { progress: 0, summary: '' },
  });

  // טוען נתונים מה-Local Storage
  useEffect(() => {
    const savedSummary = localStorage.getItem('visionSummary');
    if (savedSummary) {
      const data = JSON.parse(savedSummary);
      setAchievements(data.achievements || []);
      setInsights(data.insights || '');
      setProgressData(data.progressData || {
        weekly: { progress: 0, summary: '' },
        monthly: { progress: 0, summary: '' },
        yearly: { progress: 0, summary: '' },
      });
    }
  }, []);

  // שמירת הנתונים
  const saveSummary = () => {
    const summaryData = {
      achievements,
      insights,
      progressData,
    };
    localStorage.setItem('visionSummary', JSON.stringify(summaryData));
    alert('💾 הסיכום נשמר בהצלחה!');
  };

  // איפוס הנתונים
  const resetSummary = () => {
    setAchievements([]);
    setInsights('');
    setProgressData({
      weekly: { progress: 0, summary: '' },
      monthly: { progress: 0, summary: '' },
      yearly: { progress: 0, summary: '' },
    });
    localStorage.removeItem('visionSummary');
    alert('🗑️ הסיכום נמחק בהצלחה!');
  };

  return (
    <div className="container mx-auto p-8 bg-white rounded-md shadow-md max-w-5xl">
      <h1 className="text-3xl font-bold mb-6 text-center">📊 סיכום שבועי, חודשי ושנתי 📊</h1>

      {/* הישגים עיקריים */}
      <section className="mb-8">
        <h2 className="text-xl font-bold mb-2">🏆 הישגים עיקריים</h2>
        <div className="border p-4 rounded-md bg-gray-50">
          <ul className="list-disc pl-6 mb-4">
            {achievements.map((achievement, index) => (
              <li key={index} className="mb-1 text-lg">{achievement}</li>
            ))}
          </ul>
          <input
            type="text"
            placeholder="הוסף הישג חדש..."
            className="w-full p-3 border rounded-md"
            onKeyDown={(e) => {
              if (e.key === 'Enter' && e.target.value) {
                setAchievements([...achievements, e.target.value]);
                e.target.value = '';
              }
            }}
          />
        </div>
      </section>

      {/* תובנות אישיות */}
      <section className="mb-8">
        <h2 className="text-xl font-bold mb-2">💡 תובנות אישיות</h2>
        <div className="border p-4 rounded-md bg-gray-50">
          <textarea
            value={insights}
            onChange={(e) => setInsights(e.target.value)}
            placeholder="כתוב כאן את התובנות שלך..."
            className="w-full h-48 p-3 border rounded-md resize-none"
          />
        </div>
      </section>

      {/* גרף התקדמות + סיכומים */}
      <section className="mb-8">
        <h2 className="text-xl font-bold mb-2">📈 התקדמות לעבר החזון</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          {/* שבועי */}
          <div className="text-center border p-4 rounded-md bg-gray-50">
            <h3 className="text-lg font-semibold mb-2">🗓️ שבועי</h3>
            <input
              type="number"
              value={progressData.weekly.progress}
              onChange={(e) => setProgressData({ ...progressData, weekly: { ...progressData.weekly, progress: e.target.value } })}
              className="w-full p-2 border rounded-md"
              max={100}
              min={0}
            />
            <p>{progressData.weekly.progress}% הושלמו</p>
            <textarea
              value={progressData.weekly.summary}
              onChange={(e) => setProgressData({ ...progressData, weekly: { ...progressData.weekly, summary: e.target.value } })}
              placeholder="סיכום שבועי..."
              className="w-full h-24 p-2 border rounded-md resize-none mt-2"
            />
          </div>

          {/* חודשי */}
          <div className="text-center border p-4 rounded-md bg-gray-50">
            <h3 className="text-lg font-semibold mb-2">📅 חודשי</h3>
            <input
              type="number"
              value={progressData.monthly.progress}
              onChange={(e) => setProgressData({ ...progressData, monthly: { ...progressData.monthly, progress: e.target.value } })}
              className="w-full p-2 border rounded-md"
              max={100}
              min={0}
            />
            <p>{progressData.monthly.progress}% הושלמו</p>
            <textarea
              value={progressData.monthly.summary}
              onChange={(e) => setProgressData({ ...progressData, monthly: { ...progressData.monthly, summary: e.target.value } })}
              placeholder="סיכום חודשי..."
              className="w-full h-24 p-2 border rounded-md resize-none mt-2"
            />
          </div>
        </div>
      </section>

      {/* כפתורי פעולה */}
      <div className="flex gap-4 justify-center mt-6">
        <button onClick={saveSummary} className="bg-blue-500 text-white px-6 py-2 rounded-md">💾 שמור</button>
        <button onClick={resetSummary} className="bg-red-500 text-white px-6 py-2 rounded-md">🗑️ מחק</button>
      </div>
    </div>
  );
};

export default VisionSummary;
