import { addDoc, collection } from 'firebase/firestore';
import { auth, db } from '../firebase';

/**
 * שומר תודות בבסיס הנתונים
 * @param {Array<string>} gratitudes - רשימת התודות
 */
export const saveGratitudes = async (gratitudes) => {
  try {
    // בדיקה האם המשתמש מחובר
    if (!auth.currentUser) {
      console.error('המשתמש אינו מחובר');
      return;
    }

    // יצירת האובייקט לשמירה
    const gratitudeData = {
      userId: auth.currentUser.uid,
      gratitudes,
      date: new Date().toISOString(),
    };

    // שמירת הנתונים ב-Firestore
    await addDoc(collection(db, 'gratitude'), gratitudeData);

    console.log('תודות נשמרו בהצלחה!');
  } catch (error) {
    console.error('שגיאה בשמירת התודות:', error.message);
  }
};
