import React, { useState, useEffect } from "react";
import { db, auth } from "../firebase";
import { collection, addDoc, getDocs, query, where } from "firebase/firestore";

function Gratitude() {
  const [gratitudes, setGratitudes] = useState(["", "", ""]);
  const [gratitudeList, setGratitudeList] = useState([]);
  const [loading, setLoading] = useState(true);

  const user = auth.currentUser;

  // שליחת תודות חדשות למסד הנתונים
  const handleSaveGratitude = async () => {
    if (!user) {
      alert("יש להתחבר כדי לשמור את התודות!");
      return;
    }

    try {
      await addDoc(collection(db, "gratitude"), {
        userId: user.uid,
        gratitudes,
        date: new Date(),
      });
      alert("התודות נשמרו בהצלחה!");
      setGratitudes(["", "", ""]);
      fetchGratitudes(); // טען מחדש את הרשימה
    } catch (error) {
      console.error("שגיאה בשמירת התודות:", error.message);
    }
  };

  // שליפת רשימת התודות מהמסד
  const fetchGratitudes = async () => {
    if (!user) return;

    try {
      const q = query(
        collection(db, "gratitude"),
        where("userId", "==", user.uid)
      );
      const querySnapshot = await getDocs(q);
      const gratitudesData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setGratitudeList(gratitudesData);
    } catch (error) {
      console.error("שגיאה בטעינת התודות:", error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchGratitudes();
  }, [user]);

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4">
      <h2 className="text-3xl font-bold mb-4">הכרת תודה יומית 🙏</h2>
      <p className="mb-6 text-gray-700">כתוב שלושה דברים עליהם אתה מודה היום:</p>
      
      <div className="w-full max-w-md space-y-2 mb-4">
        {gratitudes.map((gratitude, index) => (
          <input
            key={index}
            type="text"
            value={gratitude}
            onChange={(e) => {
              const newGratitudes = [...gratitudes];
              newGratitudes[index] = e.target.value;
              setGratitudes(newGratitudes);
            }}
            placeholder={`דבר מס' ${index + 1}`}
            className="p-2 border rounded w-full"
          />
        ))}
      </div>
      
      <button
        onClick={handleSaveGratitude}
        className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
      >
        שמור תודות
      </button>

      <h3 className="text-2xl font-bold mt-8">רשימת תודות קודמות 📝</h3>
      {loading ? (
        <p>טוען...</p>
      ) : (
        <ul className="mt-4 w-full max-w-md space-y-2">
          {gratitudeList.length > 0 ? (
            gratitudeList.map((item) => (
              <li key={item.id} className="p-2 border rounded bg-white">
                <strong>{new Date(item.date.seconds * 1000).toLocaleDateString()}</strong>
                <ul className="list-disc pl-5 mt-1">
                  {item.gratitudes.map((g, i) => (
                    <li key={i}>{g}</li>
                  ))}
                </ul>
              </li>
            ))
          ) : (
            <p>אין תודות קודמות להצגה.</p>
          )}
        </ul>
      )}
    </div>
  );
}

export default Gratitude;
