import { collection, addDoc } from "firebase/firestore";
import { db, auth } from "../firebase";

// פונקציה ליצירת מסמך הכרת תודה
export const createGratitudeEntry = async (gratitudes) => {
  try {
    if (!auth.currentUser) {
      console.error("המשתמש אינו מחובר");
      return;
    }

    // יצירת אובייקט הרשומה
    const gratitudeData = {
      userId: auth.currentUser.uid,
      gratitudes,
      date: new Date().toISOString(),
      metadata: {
        status: "completed",
        tags: ["הכרת תודה", "אושר"],
      },
    };

    // הוספת מסמך לאוסף Firestore
    await addDoc(collection(db, "gratitude"), gratitudeData);
    console.log("🎉 רשומת הכרת תודה נשמרה בהצלחה!");
  } catch (error) {
    console.error("❌ שגיאה בשמירת התודות:", error.message);
  }
};
