import React, { useState } from "react";
import { createGratitudeEntry } from "../services/gratitudeService";

const GratitudePage = () => {
  const [gratitudes, setGratitudes] = useState(["", "", ""]);

  const handleInputChange = (index, value) => {
    const updatedGratitudes = [...gratitudes];
    updatedGratitudes[index] = value;
    setGratitudes(updatedGratitudes);
  };

  const handleSave = async () => {
    await createGratitudeEntry(gratitudes);
  };

  return (
    <div>
      <h1>🙏 הכרת תודה יומית</h1>
      <p>כתוב שלושה דברים שעליהם אתה מודה היום:</p>
      {gratitudes.map((gratitude, index) => (
        <input
          key={index}
          type="text"
          placeholder={`תודה מס' ${index + 1}`}
          value={gratitude}
          onChange={(e) => handleInputChange(index, e.target.value)}
        />
      ))}
      <button onClick={handleSave}>💾 שמור תודות</button>
    </div>
  );
};

export default GratitudePage;
