import React, { useState, useEffect } from 'react';

const VisionPage = () => {
  const [visionItems, setVisionItems] = useState([]); // פריטים בלוח חזון
  const [newVisionTitle, setNewVisionTitle] = useState('');
  const [newVisionDescription, setNewVisionDescription] = useState('');
  const [visionText, setVisionText] = useState('');
  const [weeklyGoals, setWeeklyGoals] = useState('');
  const [monthlyGoals, setMonthlyGoals] = useState('');
  const [yearlyGoals, setYearlyGoals] = useState('');
  const [activeTab, setActiveTab] = useState('weekly');
  const [isEditing, setIsEditing] = useState(true);

  // 🚀 טעינת נתונים מה־localStorage
  useEffect(() => {
    const savedVision = localStorage.getItem('visionBoard');
    if (savedVision) {
      setVisionItems(JSON.parse(savedVision));
    }
    const savedGoals = localStorage.getItem('visionGoals');
    if (savedGoals) {
      const data = JSON.parse(savedGoals);
      setVisionText(data.visionText || '');
      setWeeklyGoals(data.weeklyGoals || '');
      setMonthlyGoals(data.monthlyGoals || '');
      setYearlyGoals(data.yearlyGoals || '');
    }
  }, []);

  // 💾 שמירת הלוח והיעדים ב־localStorage
  const saveData = () => {
    const goalsData = {
      visionText,
      weeklyGoals,
      monthlyGoals,
      yearlyGoals,
    };
    localStorage.setItem('visionBoard', JSON.stringify(visionItems));
    localStorage.setItem('visionGoals', JSON.stringify(goalsData));
    alert('💾 כל הנתונים נשמרו בהצלחה!');
  };

  // ➕ הוספת פריט ללוח החזון
  const addVisionItem = () => {
    if (newVisionTitle.trim() === '' || newVisionDescription.trim() === '') {
      alert('נא למלא כותרת ותיאור!');
      return;
    }

    const newItem = {
      id: Date.now(),
      title: newVisionTitle,
      description: newVisionDescription,
    };

    const updatedItems = [...visionItems, newItem];
    setVisionItems(updatedItems);
    setNewVisionTitle('');
    setNewVisionDescription('');
  };

  // 🗑️ מחיקת פריט מהלוח
  const deleteVisionItem = (id) => {
    const updatedItems = visionItems.filter((item) => item.id !== id);
    setVisionItems(updatedItems);
  };

  // 🔄 איפוס הנתונים
  const resetData = () => {
    setVisionItems([]);
    setVisionText('');
    setWeeklyGoals('');
    setMonthlyGoals('');
    setYearlyGoals('');
    localStorage.removeItem('visionBoard');
    localStorage.removeItem('visionGoals');
    alert('🗑️ כל הנתונים נמחקו בהצלחה!');
  };

  return (
    <div className="container mx-auto p-8 bg-white rounded-md shadow-md max-w-5xl">
      {/* כותרת */}
      <h1 className="text-3xl font-bold mb-6 text-center">🌟 חזון אישי ויעדים 🌟</h1>

      {/* כפתורי פעולה */}
      <div className="flex gap-4 justify-center mb-6">
        <button onClick={saveData} className="bg-green-500 text-white px-4 py-2 rounded-md">
          💾 שמור הכל
        </button>
        <button onClick={resetData} className="bg-red-500 text-white px-4 py-2 rounded-md">
          🗑️ איפוס הכל
        </button>
        <button onClick={() => setIsEditing(!isEditing)} className="bg-yellow-500 text-white px-4 py-2 rounded-md">
          ✏️ {isEditing ? 'סיים עריכה' : 'ערוך'}
        </button>
      </div>

      {/* לוח חזון */}
      <section className="mb-8">
        <h2 className="text-xl font-bold mb-4">📝 לוח חזון</h2>
        <div className="mb-4 border p-4 rounded-md shadow-sm bg-gray-50">
          <input
            type="text"
            placeholder="כותרת"
            value={newVisionTitle}
            onChange={(e) => setNewVisionTitle(e.target.value)}
            className="w-full p-2 border rounded-md mb-2"
          />
          <textarea
            placeholder="תיאור"
            value={newVisionDescription}
            onChange={(e) => setNewVisionDescription(e.target.value)}
            className="w-full p-2 border rounded-md mb-2"
          />
          <button onClick={addVisionItem} className="bg-blue-500 text-white px-4 py-2 rounded-md">
            ➕ הוסף פריט ללוח
          </button>
        </div>
        {/* הצגת פריטי החזון */}
        <div className="space-y-4">
          {visionItems.map((item) => (
            <div key={item.id} className="p-4 border rounded-md shadow-md bg-white relative">
              <h3 className="text-xl font-bold mb-2">{item.title}</h3>
              <p className="text-gray-600">{item.description}</p>
              {isEditing && (
                <button
                  onClick={() => deleteVisionItem(item.id)}
                  className="bg-red-500 text-white px-2 py-1 rounded-md absolute top-2 right-2"
                >
                  🗑️ מחק
                </button>
              )}
            </div>
          ))}
        </div>
      </section>

      {/* יעדים */}
      <section>
        <h2 className="text-xl font-bold mb-4">🎯 יעדים</h2>
        <div className="flex gap-4 mb-4">
          <button onClick={() => setActiveTab('weekly')} className={`px-4 py-2 rounded-md ${activeTab === 'weekly' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>🗓️ שבועיים</button>
          <button onClick={() => setActiveTab('monthly')} className={`px-4 py-2 rounded-md ${activeTab === 'monthly' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>📅 חודשיים</button>
          <button onClick={() => setActiveTab('yearly')} className={`px-4 py-2 rounded-md ${activeTab === 'yearly' ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}>🏆 שנתיים</button>
        </div>
        <textarea value={activeTab === 'weekly' ? weeklyGoals : activeTab === 'monthly' ? monthlyGoals : yearlyGoals} onChange={(e) => setWeeklyGoals(e.target.value)} className="w-full h-32 p-2 border rounded-md"/>
      </section>
    </div>
  );
};

export default VisionPage;
